import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { NichosService } from '../services/nichos.service';

@Component({
  selector: 'app-configuracion-nicho',
  templateUrl: './configuracion-nicho.component.html',
  styleUrls: ['./configuracion-nicho.component.scss'],
  providers: [NichosService]
})
export class ConfiguracionNichoComponent implements OnInit{

   public idConfiguracion: number = 0;
   public idNicho: string = '';
   public nicho: any;
   public loading: boolean = true;

   constructor(private activatedRoute: ActivatedRoute,
               private nichosService: NichosService){

   }
  ngOnInit(): void {
    this.idNicho = this.activatedRoute.snapshot.params['idNicho'];
    this.consultaDataNicho();
  }

  /**
   * Se consulta la data del nicho
   */
   consultaDataNicho(){
     this.loading = true;
     this.nichosService.consultaNichoById(this.idNicho)
         .subscribe(response=>{
          this.nicho = response.nicho;
          this.nicho.database = response.database;
          this.loading = false;
         });
   }
}
<div fxLayout="column" *ngIf="!loading">
    <strong>{{nicho.nombre}}</strong>
    <span>{{nicho.descripcion}}</span>
     _________________________________________________________________
    <ng-container *ngIf="idConfiguracion == 0">
        <span class="pointer" (click)="idConfiguracion = 1;">Config Dase de Datos</span>
        <span class="pointer">Config General del sitio</span>
        <span class="pointer">Blog</span>
        <span class="pointer">Menu</span>
        <span class="pointer">Footer</span>
        <span class="pointer">Config FTP para subida de archivos</span>    
    </ng-container>

    <ng-container [ngSwitch]="idConfiguracion">
        <app-config-bd [nicho]="nicho" *ngSwitchCase="1" (salir)="idConfiguracion = 0"></app-config-bd>
    </ng-container> 


</div>
